    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
      if(Integer.parseInt(jTextField2.getText())<Integer.parseInt(jTextField7.getText()))
      {
      JOptionPane.showMessageDialog(null,"Please Enter valid Ammount...");
      }
      else{
              try
       {
           int Total=Integer.parseInt(jTextField2.getText())-Integer.parseInt(jTextField7.getText());
           
          Class.forName("sun.jdbc.odbc.JdbcOdbcDriver");
          con=DriverManager.getConnection("jdbc:odbc:Mydsn");
          st=con.createStatement();
          int i=st.executeUpdate("update Account set Ammount="+Total+" where Accno="+jTextField1.getText()+"");
           if(i>0)
            {
                JOptionPane.showMessageDialog(null,"Successfull Update...");
                jTextField5.setText("");
                jTextField1.setText("");
                jTextField2.setText("");
                jTextField3.setText("");
                jTextField4.setText("");
                jTextField7.setText("");
                jButton3.setEnabled(false); 
            }
            else
            {
                JOptionPane.showMessageDialog(null,"Record not saved...");
            }
       }
       catch(Exception e)
       {
           System.out.print(e);
       }}
    }//GEN-LAST:event_jButton3ActionPerformed

