    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
       jTextField7.setEnabled(true);
       jButton3.setEnabled(true);
       jTextField1.setEnabled(false);
       jButton2.setEnabled(false);
       try
       {
          Class.forName("sun.jdbc.odbc.JdbcOdbcDriver");
          con=DriverManager.getConnection("Jdbc:Odbc:Mydsn");
          st=con.createStatement();
          rs=st.executeQuery("select * from Account where accno="+jTextField1.getText()+"");
          if(rs.next())
          {
            // if(rs.getString(Accno).equals(jTextField1.getText()))
             //{
                 JOptionPane.showMessageDialog(null,"Account Number is Found...");
                 jTextField3.setText(rs.getString(2));
                 jTextField4.setText(rs.getString(3));
                 jTextField5.setText(rs.getString(4));
                 jTextField2.setText(rs.getString(7));
             }
             else
             {
                 JOptionPane.showMessageDialog(null,"NOT Found...");
             }
          //}
       }
       catch(Exception e)
       {
           System.out.print(e);
       }
    }//GEN-LAST:event_jButton2ActionPerformed

